import React from "react";
import ReactDOM from "react-dom";
import ReactFullpage from "@fullpage/react-fullpage";
import dolls from "../images/dolls.jpg"
import murakami from "../images/murakami.jpeg"


console.log(dolls)


class Inspo extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            fullpages: [
                {
                    key: "dolls",
                    image: dolls,
                    desc: "Dolls (2002), directed by Takeshi Kitano, costumes by Yohji Yamamoto"
                },
                {
                    key: "murakami",
                    image: murakami,
                    desc: "A Takeshi Murakami sculpture"
                }
            ]
        }
    }

    onLeave(origin, destination, direction) {
        console.log("onLeave", { origin, destination, direction });
        // arguments are mapped in order of fullpage.js callback arguments do something
        // with the event
    }

    render() {
        const { fullpages } = this.state;

        if(!fullpages.length) {
            return null;
        }

        return (
            <div className="Inspo">
                <ReactFullpage
                    navigation={true}
                    onLeave={this.onLeave.bind(this)}
                    licenseKey={'AA292B57-818B4C7D-BD530DCC-2171D815'}
                    render={comp => (
                        <ReactFullpage.Wrapper>
                            {fullpages.map(({ key, image, desc }) => (
                                <div key={key} className="section">
                                    <img src={image} alt={desc}/>
                                </div>
                            ))}
                        </ReactFullpage.Wrapper>
                    )
                    }
                />  
            </div>
        )
    }
}

export default Inspo
